import java.util.Random

String generateRandomString(int length) {
    def allowedChars = ('a'..'z').toList()
    def random = new Random()
    return (1..length).collect { allowedChars[random.nextInt(allowedChars.size())] }.join()
}

String randomString = generateRandomString(5)
println "Generated Random String: ${randomString}"
testRunner.testCase.testSuite.setPropertyValue("tagName", randomString)
testRunner.testCase.testSuite.setPropertyValue("tagColor", "#000000")

con:configuration.scriptText:
import com.eviware.soapui.support.GroovyUtils

def testSuite = context.testCase.testSuite
def tagName = testSuite.getPropertyValue("tagName")
def tagColor = testSuite.getPropertyValue("tagColor")

def jsonText = messageExchange.getResponseContent()
def json = new groovy.json.JsonSlurper().parseText(jsonText)

assert json.tags instanceof List && json.tags.size() == 1
assert json.tags[0].name == tagName
assert json.tags[0].tag_fg == tagColor
assert json.tags[0].tag_bg == tagColor

import com.eviware.soapui.support.GroovyUtils

def testSuite = context.testCase.testSuite
